using namespace std;


class UnionFind {
    std::vector<int> p;

public:
    UnionFind(int n) : p(n, -1) {}

    int root(int x) { return p[x] < 0 ? x : p[x] = root(p[x]); }

    bool same(int x, int y) { return root(x) == root(y); }

    bool unite(int x, int y) {
        x = root(x);
        y = root(y);
        if (x == y) return false;
        if (p[y] < p[x]) std::swap(x, y);
        if (p[x] == p[y]) --p[x];
        p[y] = x;
        return true;
    }
};

int main() {
    int n, m;
    while (cin >> n >> m && n) {
        UnionFind uf(n);
        bool hasLoop = false;
        vector<int> deg(n, 0);
        for (int i = 0; i < m; i++) {
            int a, b;
            cin >> a >> b;
            --a, --b;
            deg[a]++;
            deg[b]++;
            hasLoop |= !uf.unite(a, b);
        }
        if( *max_element(deg.begin(), deg.end()) > 2 || hasLoop ){
            cout << "no" << endl;
        }else{
            cout << "yes" << endl;
        }
    }

}

