        g.init(n+1);
        vector<int> cnt(n+1,0);
        REP(i,m){
            int a = v[i].first, b=v[i].second;
            if(!g.is_same(a,b) && cnt[a]<2 && cnt[b]<2){
                g.unite(a,b);
                cnt[a]++;
                cnt[b]++;
            }
            else {
                ok = false;
                break;
            }
        }
        cout << (ok?"yes":"no") << endl;
    }
    return 0;
}

