struct UnionFind{
    vector<int> tree;

    UnionFind(int n): tree(n+1, -1) {};
    int root(int i) {return tree[i] < 0 ? i : tree[i] = root(tree[i]); }

    bool unite(int i, int j){
        i = root(i), j = root(j);
        if(i == j) return false;
        tree[i] += tree[j], tree[j] = i;
        return true;
    }
};


int main(){
    int n, m;

    while(cin >> n >> m, n != 0){
        vector<vector<int> > graph(n);

