            a--;
            b--;
            graph[a].push_back(b);
            graph[b].push_back(a);
            flag = (flag && uf.unite(a, b));
        }

        for(int i = 0;i < n;i++){
            if(graph[i].size() >= 3){
                flag = false;
                break;
            }
        }

        if(flag){
            cout << "yes" << endl;
