    for(int i=0,a,b;i<m;i++){
      cin>>a>>b;
      in[a].push_back(P(b,in[b].size()));
      in[b].push_back(P(a,in[a].size()-1));
      if(in[a].size()/3||in[b].size()/3) ans=0;
    }

    for(int i=1;i<=n;i++) check(i);
    cout <<(ans? "yes":"no")<<endl;
  }
  return 0;
}

