#include <iostream>
#include <vector>
using namespace std;

bool ContainLoop( int c, int p, const vector< vector<int> >& g, vector<int>& visit ) {

    visit[c] = true;

    for ( int dst : g[c] ) {
	if ( dst == p ) continue;
	if ( visit[dst] ) return true;
	if ( ContainLoop( dst, c, g, visit ) ) return true;
    }

    return false;
}

int main() {
    int n, m;
    while ( cin >> n >> m ) {

	if ( !n && !m ) break;

	vector<int> cnt( n );
	vector< vector<int> > g( n );
	vector<int> visit( n );

	bool ok = true;
	int s, t;
	for ( int i=0; i<m; ++i ) {
	    cin >> s >> t;
	    --s, --t;
	    ++cnt[s];
	    ++cnt[t];
	    if ( cnt[s] > 2 || cnt[t] > 2 ) ok = false;
	    g[s].push_back( t );
	    g[t].push_back( s );
	}

	if ( !ok ) cout << "no" << endl;
	else {
	    int i = 0;
	    for ( ; i<n; ++i ) {
		if ( !visit[i] && ContainLoop( i, -1, g, visit ) ) break;;
	    }
	    if ( i == n ) cout << "yes" << endl;
	    else cout << "no" << endl;

	}

    }
    return 0;
}

