			if (same(a, b)) ok = false;
			merge(a, b);
			deg[a]++; deg[b]++;
			if (deg[a] > 2 || deg[b] > 2) ok = false;
			s.insert(make_pair(a, b));
			s.insert(make_pair(b, a));
		}

		printf("%s\n", ok ? "yes" : "no");
	}

	return (0);
}

