#define rep(i,n) REP(i,0,n)

using namespace std;

const int MAX_V = 100010;
const string YES = "yes";
const string NO  = "no";

int V,E;
int degree[MAX_V];
int par[MAX_V],ones[MAX_V],two[MAX_V];

int find(int x) { 
  if( x == par[x] ) return x;
  return par[x] = find(par[x]);
}

void unit(int x,int y){
  x = find(x), y = find(y);
  if( x != y ) par[x] = y;
}

void compute(){
  if( E >= V ) { cout << NO << endl; return; }
  rep(i,V) if( degree[i] >= 3 ) { cout << NO << endl; return; }
  rep(i,V) {
    int group_index = find(i);
    if( degree[i] == 1 ) ++ones[group_index];
    else if( degree[i] == 2 ) ++two[group_index];
  }
  rep(i,V) if( ones[i] >= 3 ) { cout << NO << endl; return; }
  cout << YES << endl;
}

int main(){
  int u,v;
  while( scanf("%d %d",&V,&E), V|E ){
    rep(i,V) degree[i] = ones[i] = two[i] = 0, par[i] = i;
    typedef pair<int,int> ii;
    set<ii> S;
    bool success = true;
    rep(i,E){
      scanf("%d %d",&u,&v);
      --u, --v;
      if( u > v ) swap(u,v);
      ++degree[u], ++degree[v];
      if( u == v ) success = false;
      if( S.count(ii(u,v)) ) success = false;
      S.insert(ii(u,v));
      unit(u,v);
    }
    if( !success ) { cout << NO << endl; continue; }
    compute();
  }
  return 0;
}

