int solve(vector<int>haves, vector<int>nums) {
	map<pair<vector<int>,int>,int>mp;
	mp[make_pair(nums,0)]=1;

	int ans=1;
	queue<pair<vector<int>,int>>que;
	que.push(make_pair(nums,0));
	while (!que.empty()) {
		auto atop(que.front());
		que.pop();

		int x=atop.second;
		vector<int>v(atop.first);

		int cost=mp[make_pair(atop.first,atop.second)];
		ans=max(ans,cost);
		for (int i = 0; i < haves.size(); ++i) {
			if (v[i]){
				if (x+haves[i] >= 11) {

				}
				else if (x + haves[i] == 10) {
					v[i]--;
					auto np=make_pair(v,0);
					if (mp[np] < cost + 1) {
						mp[np]=cost+1;
						que.push(np);
					}
					v[i]++;
				}
				else {
					v[i]--;
					auto np=make_pair(v,x+haves[i]);
					if (mp[np] < cost) {
						mp[np]=cost;
						que.push(np);
					}
					v[i]++;
				}
			}
		}
	}
	return ans-1;
}
int dfs(const int now, const int pre,const vector<vector<int>>&edges, vector<int>&memo) {
	if(memo[now])return false;
	else {
		memo[now]=true;
		for (auto e : edges[now]) {
			if (e != pre) {
				int k=dfs(e,now,edges,memo);
				if(!k)return false;
			}
		}
	}
	return true;
}
