int par[N],ran[N];

int find(int x){
  if(par[x]==x) return x;
  else return par[x]=find(par[x]);
}

void unite(int x,int y){
  if(x<y) swap(x,y);
  x=find(x);
  y=find(y);
  if(x==y) return;
  if(ran[x]<ran[y]) par[x]=y;
  else{
    par[y]=x;
    if(ran[x]==ran[y]) ran[x]++;
  }
}

bool same(int x,int y){
  return find(x)==find(y);
}

int main(){
  int n,m,u,v,flag,f2;
  vector<int> edge[N];
  while(1){
    cin>>n>>m;
    if(!n&&!m) break;
    for(int i=0;i<m;i++){
      cin>>u>>v;
      edge[u-1].push_back(v-1);
      edge[v-1].push_back(u-1);
    }
    flag=f2=0;
    for(int i=0;i<n;i++){
      if(edge[i].size()>=3) flag=1;
      if(edge[i].size()==2) f2=1;
    }
    if(f2&&!flag){
      for(int i=0;i<n;i++) par[i]=i,ran[i]=0;
      for(int i=0;i<n;i++){
	for(int j=0;j<edge[i].size();j++){
	  if(j==1&&same(edge[i][0],edge[i][1])) flag=1;
	  else unite(i,edge[i][j]);
	}
      }      
    }
    if(flag) cout<<"no"<<endl;
    else cout<<"yes"<<endl;
