using namespace std;
typedef pair<int,int> P;
int ans,used[100010];
vector<P>in[100010];
int n,m;

void check(int pos){
  if(used[pos]||!ans)return;
  used[pos]=1;
  for(int i=0;i<in[pos].size();i++){
    int nx=in[pos][i].first,idx=in[pos][i].second;
    if(used[nx])ans=0;
    in[pos].erase(in[pos].begin()+i);
    in[nx].erase(in[nx].begin()+idx);
    check(nx);
    i=-1;
  }
}

int main(){
  while(1){
    cin>>n>>m;
    if(!n&&!m)break;
    for(int i=0;i<=n;i++)in[i].clear(),used[i]=0;

    ans=1;
    for(int i=0,a,b;i<m;i++){
      cin>>a>>b;
      in[a].push_back(P(b,in[b].size()));
      in[b].push_back(P(a,in[a].size()-1));
      if(in[a].size()/3||in[b].size()/3) ans=0;
    }

    for(int i=1;i<=n;i++) check(i);
    cout <<(ans? "yes":"no")<<endl;
  }
  return 0;
}

