using namespace std;

bool dfs(int node, int prenode, vector< vector< int > > &becky, vector<bool> &used){
  bool res = true;
  used[node] = 1;
  for(int i=0; i < (signed int)becky[node].size(); i++){
    if(becky[node][i] != prenode && used[becky[node][i]]){
      return false;
    }
    if(used[becky[node][i]] == false)
      res = dfs(becky[node][i], node, becky, used);
    if(res == false) break;
  }
  return res;
}

int main(void){
  int n, m;
  while(cin >> n >> m, n | m){
    vector< vector< int > > becky(n);
    vector<bool> used(n, 0);
    for(int i=0; i < m; i++){
      int a, b; cin >> a >> b;
      a--; b--;
      becky[a].push_back(b);
      becky[b].push_back(a);
    }
    bool ans = true;
    for(int i=0; i < n; i++){
      if(ans && used[i] == false){
	ans = dfs(i, -1, becky, used);
      }
    }
    if(ans) cout << "yes" << endl;
    else    cout << "no"  << endl;
  }
  return 0;
}

