typedef struct P{
	int u,v;
	bool operator<(const P &r){
		return u < r.u;
	}
	void swap(){
		if(u <= v) return;
		int t = u;
		u = v;
		v = t;
	}
}P;

int n,m;
bool f;
P p[1000000];

int main(){
	while(scanf("%d%d",&n,&m), n != 0){
		for(int i = 0; i < m; i++){
			scanf("%d%d",&p[i].u,&p[i].v);
			p[i].swap();
		}
		sort(p,p+m);
		f = true;
		for(int i = 1; i < m; i++){
			if(p[i-1].v >= p[i].u || p[i-1].u == p[i].u){
				f = false;
				break;
			}
		}
		printf("%s\n", f ? "yes" : "no");
	}
	return 0;
}

