bool findEndPoint(int idx,vector<bool> &v,int pre=-1){
	if(v[idx]){
		return false;
	}
	v[idx] = true;
	if(pre != -1 && vl[idx].size() == 1){
		return true;
	}

	if(vl[idx][0] == pre){
		return findEndPoint(vl[idx][1],v,idx);
	}
	else{
		return findEndPoint(vl[idx][0],v,idx);
	}
}
bool loopNotExist(int idx,vector<bool> &v){
	if(vl[idx].size() == 0){
		return true;
	}
	if(vl[idx].size() == 1){
		return findEndPoint(idx,v);
	}
	bool b1 = findEndPoint(vl[idx][0],v,idx);
	bool b2 = findEndPoint(vl[idx][1],v,idx);
	return b1 && b2;
}

