      scanf("%d%d",&a,&b); a--; b--;
      check(++deg[a]<=2);
      check(++deg[b]<=2);
      check(!uf.same(a,b));
      uf.unite(a,b);
    }

    if(f)puts("yes");
    else puts("no");
  }
}

