bool solve(const vector<vector<int> >& edges)
{
    int n = edges.size();

    UnionFindTree uft(n);
    for(int a=0; a<n; ++a){
        if(edges[a].size() > 2)
            return false;
        for(int b : edges[a]){
            if(a < b){
                if(uft.same(a, b))
                    return false;
                uft.unite(a, b);
            }
        }
    }
    return true;
}

