class UnionFind {
    int n;
    vector<int> uni;
    int forest_size;
public:
    explicit UnionFind(int n) : n(n), uni(static_cast<u32>(n), -1), forest_size(n) {};

    int root(int a){
        if (uni[a] < 0) return a;
        else return (uni[a] = root(uni[a]));
    }

    bool unite(int a, int b) {
        a = root(a);
        b = root(b);
        if(a == b) return false;
        if(uni[a] > uni[b]) swap(a, b);
        uni[a] += uni[b];
        uni[b] = a;
        forest_size--;
        return true;
    }
    int size(){ return forest_size; }
    int size(int i){ return -uni[root(i)]; }
    bool same(int a, int b) { return root(a) == root(b); }
};

void solve(int n, int m){
    vector<int> deg(n);
    UnionFind uf(n);
    int ok = 1;
    for (int i = 0; i < m; ++i) {
        int a, b;
        scanf("%d %d", &a, &b);
        a--; b--;
        if(!uf.unite(a, b)) ok = 0;

        deg[a]++; deg[b]++;
    }
    for (int i = 0; i < n; ++i) {
        if(deg[i] > 2) ok = 0;
    }
    puts(ok ? "yes" : "no");
};

int main() {
    int n, m;
    while(cin >> n >> m, n) solve(n, m);
    return 0;
}

