int par[N],ran[N];
bool visited[N];

int find(int x){
  if(par[x]==x) return x;
  else return par[x]=find(par[x]);
}

void unite(int x,int y){
  if(x<y) swap(x,y);
  x=find(x);
  y=find(y);
  if(x==y) return;
  if(ran[x]<ran[y]) par[x]=y;
  else{
    par[y]=x;
    if(ran[x]==ran[y]) ran[x]++;
  }
}

