  int n,m,a[100010],b[100010],r[100010],i,d[100010][2],f[100010],s;
  while(scanf("%d %d",&n,&m),n){
    for(i=0;i<m;i++)scanf("%d %d",&a[i],&b[i]);
    if(n<=m)goto END;
    for(i=0;i<=n;i++)r[i]=f[i]=0;
    for(i=0;i<m;i++){
      d[a[i]][r[a[i]]++]=b[i];
      d[b[i]][r[b[i]]++]=a[i];
      if(r[a[i]]==3||r[b[i]]==3)goto END;
    }
    for(i=1;i<=n;i++){
      if(r[i]%2==0)continue;
      r[i]=2;
      s=i;
      while(r[s]==2){
	r[s]=0;
	f[s]=1;
	if(f[d[s][0]])s=d[s][1];
	else          s=d[s][0];
      }
      r[s]=0;
      f[s]=1;
    }
    for(i=0;i<n&&r[i+1]==0;i++);
    printf("%s\n",i-n?"no":"yes");
    continue;
  END:;
    printf("no\n");
  }
  return 0;
}

fun main(args:Array<String>):Unit {
    while (true) {
        val (n, m) = readLine()!!.trim().split(' ').map(String::toInt)
        if (n == 0 && m == 0) return
        val stone = Array(n) { UnionFindTree() }
        val edgeCount = Array(n){0}
        var canMake = true
        repeat(m) {
            val (u, v) = readLine()!!.trim().split(' ').map(String::toInt)
            canMake = canMake && !stone[u - 1].isSameUnion(stone[v - 1])
            stone[u - 1].merge(stone[v - 1])
            edgeCount[u - 1]++
            edgeCount[v - 1]++
        }
        println(if (canMake && edgeCount.none{it > 2}) "yes" else "no")
    }
}
class UnionFindTree{
    private var mParent:UnionFindTree? = null
    private fun parent():UnionFindTree {
        return when(mParent){
            null -> this
            else -> {
                mParent = mParent?.parent()
                mParent !!
            }
        }
    }
    fun isSameUnion(other:UnionFindTree):Boolean {
        return parent() == other.parent()
    }
    fun merge(other:UnionFindTree):Unit {
        if (!isSameUnion(other)){
            parent().mParent = other.parent()
        }
    }
}

