namespace _0251
{
    class Program
    {
        static void Main(string[] args)
        {
            while (true)
            {
                int[] x = Console.ReadLine().Split().Select(int.Parse).ToArray();
                if (x[0] == 0 && x[1] == 0) break;
                int[] used = new int[x[0]];
                for (int i = 0; i < x[1]; i++)
                {
                    int[] n = Console.ReadLine().Split().Select(int.Parse).ToArray();
                    used[n[0] - 1]++; used[n[1] - 1]++;
                }
                if (used.Max() < 3)
                {
                    int two = 0;
                    for (int i = 0; i < x[0]; i++)
                    {
                        if (used[i] == 2) two++;
                    }
                    Console.WriteLine(x[0] - two >= 2 ? "yes" : "no");
                }
                else Console.WriteLine("no");
            }
        }
    }
}

