using namespace std;

#define NUM 30001


int N,M;
int table[NUM];
vector<int> V;


void func(){

	V.clear();

	table[0] = 0;
	int ans = 0;

	int left,right,m;
	int near_num;

	for(int i = 1; i <= N; i++){
		scanf("%d",&table[i]);
		table[i] += table[i-1];
		table[i] %= M;

		if(i == 1){
			ans = table[i];
			V.push_back(table[i]);
			continue;
		}

		ans = max(ans,(table[i]-V[0]+M)%M);

		left = 0, right = V.size()-1,m = (left+right)/2;
		near_num = -1;

		while(left <= right){
			if(V[m] > table[i]){
				near_num = V[m];
				right = m-1;
			}else{
				left = m+1;
			}
			m = (left+right)/2;
		}

		if(near_num != -1){
			ans = max(ans,(table[i]-near_num+M)%M);
		}
		V.push_back(table[i]);
		sort(V.begin(),V.end());
		V.erase(unique(V.begin(),V.end()),V.end());
	}

	printf("%d\n",ans);

}


int main(){

	while(true){
		scanf("%d %d",&N,&M);
		if(N == 0 && M == 0)break;

		func();
	}

	return 0;
}

