using namespace std;
using ld =long double;
const ld eps = 1e-9;

#define Seg_Max_N (1<<18) 

using Value = pair<long long int, long long int>;
const Value ini = (make_pair(-1e9, 1e9));
struct segtree {
	int N;
	vector<Value>dat;

	segtree() {}
	segtree(int n) :dat(2 * Seg_Max_N) {
		N = 1;
		while (N < n) N *= 2;
		for (int i = 0; i < 2 * N - 1; i++) {
			dat[i] = ini;
		}
	}
	Value connect(const Value&l, const Value&r) {
		return make_pair(max(l.first, r.first), min(l.second, r.second));
	}
	// update k th element
	void update(int k, Value a) {
		k += N - 1;
		dat[k] = a;

		while (k > 0) {
			k = (k - 1) / 2;
			const Value al(dat[k * 2 + 1]);
			const Value ar(dat[k * 2 + 2]);
			dat[k] = connect(al, ar);
		}
	}
	// min [a, b)
	Value  query(int a, int b) { return query(a, b, 0, 0, N); }
	Value  query(int a, int b, int k, int l, int r) {
		if (r <= a or b <= l) return ini;
		if (a <= l and r <= b) return dat[k];
		const int m = (l + r) / 2;
		const Value al(query(a, b, k * 2 + 1, l, m));
		const Value ar(query(a, b, k * 2 + 2, m, r));
		return connect(al, ar);
	}
};


int main()
{
	while (true) {
		int N,M;cin>>N>>M;
		if(!N)break;
		segtree seg(M);
		seg.update(0,make_pair(0,0));
		int fin_ans=0;

		int sum=0;
		for (int i = 0; i < N; ++i) {
			int k;cin>>k;
			k%=M;

			sum=(sum+k)%M;
			int ans;
			auto rp=seg.query(sum+1,M);
			if (rp.first <0) {
				auto lp=seg.query(0,sum+1);
				ans= sum -lp.second;
			}
			else {
				ans=(sum +M)-rp.second;
			}
			seg.update(sum,make_pair(sum, sum));
			fin_ans=max(ans,fin_ans);
		}
		cout<<fin_ans<<endl;
	}
	return 0;
}

