			SET.insert(table[i]);
			continue;
		}
		ans = max(ans,table[i]);

		auto it = SET.upper_bound(table[i]);
		if(it != SET.end()){
			ans = max(ans,(table[i]-*it+M)%M);
		}
		SET.insert(table[i]);
	}

	printf("%d\n",ans);

}


