int solve(const vector<int>& a, int m)
{
    int n = a.size();
    int ans = 0;
    int index = 0;
    BinaryIndexedTree bit(2*m);

    for(int i=0; i<n; ++i){
        bit.add(index, 1);
        bit.add(m+index, 1);
        index += m - a[i];
        index %= m;

        int left = 0;
        int right = m - 1;
        while(left < right){
            int mid = (left + right + 1) / 2;
            if(bit.sum(index, index+mid-1) <= i)
                left = mid;
            else
                right = mid - 1;
        }
        ans = max(ans, left);
    }

    return ans;
}

