typedef pair<int,int> pii;
typedef vector<int> vi;

//Segment Tree for Range Maximum Query
class SegmentTree{
  int n;
  vi node;
public:
  SegmentTree(int n_){
    n=1;
    while(n<n_)n*=2;
    node = vi(2*n-1,-1e9);
  }

  void update(int k, int v){
    int cur = k + n-1;
    node[cur] = v;
    while(cur>0){
      cur = (cur-1)/2;
      node[cur] = max(node[cur*2+1],node[cur*2+2]);
    }
  }

  //return maximum value in [a,b). ( [l,r) is interval in which k is.)
  int RMQ(int a,int b,int k=0,int l=0,int r=0){
    if(l>=r)r = n;
    if(r<=a || b<=l)return -1e9;
    if(a<=l && r<=b)return node[k];
    int vl = RMQ(a,b,2*k+1,l,(l+r)/2);
    int vr = RMQ(a,b,2*k+2,(l+r)/2,r);
    return max(vl,vr);
  }
};

