    if( DEBUG ) { puts(""); cout << arr[i] << " -th" << endl; }
    int low  = sub;
    int high = ( m - 1 + sub ) % m;
    assert( convert(low,sub) == 0 );
    assert( convert(high,sub) == m-1 );
    if( DEBUG)cout << low << "," << high << " [" << convert(low,sub) << "," << convert(high,sub) << "]" << endl;

    assert( low != high );

    if( low < high ) {
