      int temp = getMax(low,m-1);
      if( DEBUG){
        cout << "search[" << low << "," << m-1 << "]" << endl;
        cout << "temp = " << temp << " " << convert(temp,sub) << endl;
      }
      if( temp != -1 ) maxi = max(maxi,convert(temp,sub));
      temp = getMax(0,high);
