using namespace std;
typedef long long ll;
typedef pair<ll,ll> pll;
typedef vector<ll> vl;

//Segment Tree for Range Maximum Query
class SegmentTree{
  int n;
  vl node;
public:
  SegmentTree(int n_){
    n=1;
    while(n<n_)n*=2;
    node = vl(2*n-1,-1e9);
  }

  void update(int k, ll v){
    int cur = k + n-1;
    node[cur] = v;
    while(cur>0){
      cur = (cur-1)/2;
      node[cur] = max(node[cur*2+1],node[cur*2+2]);
    }
  }

  //return maximum value in [a,b). ( [l,r) is interval in which k is.)
  ll RMQ(int a,int b,int k=0,int l=0,int r=0){
    if(l>=r)r = n;
    if(r<=a || b<=l)return -1e9;
    if(a<=l && r<=b)return node[k];
    ll vl = RMQ(a,b,2*k+1,l,(l+r)/2);
    ll vr = RMQ(a,b,2*k+2,(l+r)/2,r);
    return max(vl,vr);
  }
};

int main(){
  int n,m;
  while(scanf("%d%d",&n,&m), n){
    vl s(n+1,0);
    rep(i,n){
      scanf("%lld",&s[i+1]);
      (s[i+1] += s[i]) %= m;
    }

    vector<pll> ord(n);
    rep(i,n){
      ord[i] = pll(s[i+1],i);
    }
    sort(ord.begin(),ord.end());

    SegmentTree st(n);
    ll res = 0;
    rep(i,n){
      ll v = ord[i].first, p = ord[i].second;
      res = max(res, v);
      res = max(res, st.RMQ(p+1,n) + m-v);
      st.update(p,v);
    }
    printf("%lld\n",res);
  }
}

