#include <bits/stdc++.h>

using namespace std;

const int INF = 1 << 28;

struct SegNode
{
  int v;

  SegNode(int v) : v(v) {}

  SegNode operator*(const SegNode &r) const
  {
    return (v > r.v ? *this : r);
  }
} e(-(1 << 30));

struct SegmentTree
{
  int sz;
  vector< SegNode > seg;

  SegmentTree(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    seg.assign(2 * sz - 1, e);
  }

  void update(int k, const SegNode &x)
  {
    k += sz - 1;
    seg[k] = x;
    while(k > 0) {
      k = (k - 1) >> 1;
      seg[k] = seg[2 * k + 1] * seg[2 * k + 2];
    }
  }

  SegNode query(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return (e);
    if(a <= l && r <= b) return (seg[k]);
    return (query(a, b, 2 * k + 1, l, (l + r) >> 1) * query(a, b, 2 * k + 2, (l + r) >> 1, r));
  }

  SegNode query(int a, int b)
  {
    return (query(a, b, 0, 0, sz));
  }
};


int main()
{
  int N, M, X[30000];

  while(cin >> N >> M, N) {
    for(int i = 0; i < N; i++) {
      cin >> X[i];
      X[i] %= M;
    }

    vector< int > first(M, INF), last(2 * M, -INF);
    first[0] = -1;
    int sum = 0;
    for(int i = 0; i < N; i++) {
      sum += X[i];
      sum %= M;
      first[sum] = min(first[sum], i);
      last[sum] = max(last[sum], i);
      last[M + sum] = max(last[M + sum], i);
    }

    int ret = 0;
    SegmentTree seg(N);
    int ptr = 0;
    for(int i = 0; i < M; i++) {
      while(ptr - i < M) {
        if(last[ptr] >= 0) {
          seg.update(last[ptr], ptr);
        }
        ++ptr;
      }
      ret = max(ret, seg.query(first[i] + 1, M).v - i);
    }
    cout << ret << endl;
  }
}

