		for(int i=1;i<=n;i++){
			long long a; scanf("%lld",&a);
			x[i] = (x[i-1]+a)%m;
		}
		set<long long>s; s.insert(x[n]); ll r = 0;
		for(int i=n-1;i>=0;i--){
			set<long long>::iterator it = s.lower_bound(x[i]);
			if(it != s.begin()){
				it--;
				ll v = ((*it) - x[i]+m)%m;
				r = max(r,v);
			}
			else{
				it = s.end(); it--;
				ll v = ((*it) - x[i]+m)%m;
				r = max(r,v);
			}
			s.insert(x[i]);
		}
		cout << r << endl;
	}
}

