struct Star {
  int x, y, d;
};

int minX[200000 * 2 - 1] = {}, maxX[200000 * 2 - 1] = {},
minY[200000 * 2 - 1] = {}, maxY[200000 * 2 - 1] = {};
int n, nn = 1;

void update(int k, int a, int c[]) {
  k += nn - 1;
  c[k] = a;

  while(k > 0) {
    k = (k - 1) / 2;
    c[k] = min(c[k * 2 + 1], c[k * 2 + 2]);
  }
}

int query(int a, int b, int k, int l, int r, int c[]) {
  if(r <= a || b <= l) return INT_MAX;
  if(a <= l && r <= b) return c[k];

  int vl = query(a, b, k * 2 + 1, l, (l + r) / 2, c);
  int vr = query(a, b, k * 2 + 2, (l + r) / 2, r, c);

  return min(vl, vr);
}

bool asc(Star &l, Star &r) {
  return l.d < r.d;
}

int main() {
  ll d, ans = 0;
  cin >> n >> d;
  vector<Star> v(n);

  while(nn < n) nn *= 2;

  rep(i, 0, nn * 2 - 1) {
    minX[i] = INT_MAX;
    minY[i] = INT_MAX;
    maxX[i] = INT_MAX;
    minX[i] = INT_MAX;
  }

  rep(i, 0, n) cin >> v[i].x >> v[i].y >> v[i].d;

  sort(v.begin(), v.end(), asc);
  rep(i, 0, n) {
    update(i, v[i].x * -1, maxX);
    update(i, v[i].x, minX);
    update(i, v[i].y * -1, maxY);
    update(i, v[i].y, minY);
  }

  // rep(i, 0, n) cout << v[i].x << " " << v[i].y << " " << v[i].d << endl;;

  // rep(i, 0, nn * 2 - 1) cout << minX[i] << endl;

  int ld = v[0].d, lp = 0;
  rep(i, 1, n) {
    if(v[i].d - ld > d) {
      rep(j, lp + 1, i + 1) {
        if(v[i].d - v[j].d <= d) {
          lp = j;
          ld = v[lp].d;
          break;
        }
      }
    }
    int hx = query(lp, i + 1, 0, 0, nn, maxX) * -1;
    int lx = query(lp, i + 1, 0, 0, nn, minX);
    int hy = query(lp, i + 1, 0, 0, nn, maxY) * -1;
    int ly = query(lp, i + 1, 0, 0, nn, minY);
    if(lp != i) ans = max(ans, ((hx - lx) * (ll)(hy - ly)));
    // cout << lp << " " << i << endl;
    // cout << hx << " " << lx << " " << hy << " " << ly << endl;
    // cout << ans << endl;
  }

  cout << ans << endl;
}

