bool ans[200000];

struct NODE{
  int min_d;
  int max_d;
  int min_x;
  int min_y;
  int max_x;
  int max_y;

  NODE(){
    min_d = INF;
    max_d = 0;
    min_x = INF;
    max_x = 0;
    min_y = INF;
    max_y = 0;
  }

  NODE(int a,int b,int c,int d,int e,int f){
    min_d = a;
    max_d = b;
    min_x = c;
    max_x = d;
    min_y = e;
    max_y = f;
  }

  NODE operator+ (const NODE &B) const{
    int a = min(min_d,B.min_d);
    int b = max(max_d,B.max_d);
    int c = min(min_x,B.min_x);
    int d = max(max_x,B.max_x);
    int e = min(min_y,B.min_y);
    int f = max(max_y,B.max_y);

    return NODE(a,b,c,d,e,f);
  }
};

NODE data[1 << 19];
int segn2 = 262144;


void set(int k,pair<int,pair<int,int> > p){
  int d = p.first;
  int x = p.second.first;
  int y = p.second.second;

  data[k+segn2-1] = NODE(d,d,x,x,y,y);
}

void init(){
  for(int i = segn2-2;i>=0;i--){
    data[i] = data[i*2+1] + data[i*2+2];
  }
}

NODE query(int a,int b,int k=0){
  if(data[k].max_d < data[k].min_d) return NODE(INF,0,INF,0,INF,0);
  if(data[k].max_d < a || b < data[k].min_d) return NODE(INF,0,INF,0,INF,0);

  if(a<= data[k].min_d && data[k].max_d <= b) return data[k];

  return query(a,b,k*2+1) + query(a,b,k*2+2);
}

int main(){
  int n,d;
  int x,y,b;
  vector<pair<int,pair<int,int> > > in;

  scanf("%d%d",&n,&d);

  segn2 = 1;
  while(segn2<n) segn2*=2;

  for(int i=0;i<n;i++){
    scanf("%d%d%d",&x,&y,&b);
    in.push_back(make_pair(b,make_pair(x,y)));
  }

  sort(in.begin(),in.end());

  for(int i=0;i<n;i++){
    set(i,in[i]);
  }

  init();

  ll ans = 0;

  for(int i=0;i<n;i++){
    NODE a = query(in[i].first,in[i].first+d);
    ll calc = (ll)(a.max_x - a.min_x)*(a.max_y - a.min_y);
    ans = max(ans,calc);
  }

  cerr << ans << endl;
  return 0;
}

