#define INF (1<<29)

struct SegmentTree {
	int init_val;
	int n;
	vector<int> dat;

	SegmentTree(int _n) {
		init_val = INF;
		n = 1;
		while (n < _n) n *= 2;
		dat.clear();
		dat.resize(2 * n - 1, init_val);
	}

	void update(int k, int a) {
		k += n - 1;
		dat[k] = min(dat[k], a);

		while (k > 0) {
			k = (k - 1) / 2;
			dat[k] = min(dat[2 * k + 1], dat[2 * k + 2]);
		}
	}

	int query(int a, int b, int k, int l, int r) {
		if (r <= a || b <= l) return init_val;
		if (a <= l && r <= b) return dat[k];
		return min(query(a, b, k * 2 + 1, l, (l + r) / 2), query(a, b, k * 2 + 2, (l + r) / 2, r));
	}

	int query(int a, int b) {
		return query(a, b, 0, 0, n);
	}
};

typedef pair<int, int> pii;
typedef pair<int , pii> pip;
typedef SegmentTree Seg;
typedef long long ll;

int main() {
	int n, d;
	cin >> n >> d;
	vector<pip> stars(n);
	REP(i, n) {
		int x, y, b;
		scanf("%d %d %d", &x, &y, &b);
		stars[i] = pip(b, pii(x, y));
	}
	sort(stars.begin(), stars.end());

	Seg up(n), down(n), right(n), left(n);
	REP(i, n) {
		int x = stars[i].second.first;
		int y = stars[i].second.second;

		up.update(i, -y);
		down.update(i, y);
		right.update(i, -x);
		left.update(i, x);
	}

	/*
	puts("");
	REP(i, n) {
		cout << stars[i].first << " " << stars[i].second.first << " " << stars[i].second.second << endl;
	}
	puts("");
	*/

	int st = 0, en = 0;
	ll ans = -INF;

	while (st < n) {
		if (en - st > 0 && stars[en].first - stars[st].first <= d) {
			ll  s = (ll)(-up.query(st, en + 1) - down.query(st, en + 1))
				    * (-right.query(st, en + 1) - left.query(st, en + 1));
			ans = max(ans, s);

			if (en == n - 1) st++;
			else en++;

		}
		else if (stars[en].first - stars[st].first > d) st++;
		else if (en < n - 1) en++;
		else st++;
	}

	cout << ans << endl;

	return 0;
}

