struct Star {
  int x, y, b;
  Star(int x, int y, int b) : x(x), y(y), b(b) {}
  bool operator<(const Star& x) const {
    return b < x.b;
  }
};

SegT<int, 200200> xmax(INT_MIN, [](int a, int b){ return max(a, b); });
SegT<int, 200200> xmin(INT_MAX, [](int a, int b){ return min(a, b); });
SegT<int, 200200> ymax(INT_MIN, [](int a, int b){ return max(a, b); });
SegT<int, 200200> ymin(INT_MAX, [](int a, int b){ return min(a, b); });

signed main()
{

  int N = in();
  int d = in();
  vector<Star> vs;
  for (int i = 0; i < N; i++){
    int x = in();
    int y = in();
    int b = in();
    vs.emplace_back(x, y, b);
  }
  sort(vs);
  for (int i = 0; i < N; i++){
    xmin.update(i, vs[i].x);
    xmax.update(i, vs[i].x);
    ymin.update(i, vs[i].y);
    ymax.update(i, vs[i].y);
  }

  int maxi = 0;
  for (int i = 0; i < N; i++){
    int t = ubnd(vs, Star(0, 0, vs[i].b + d));
    int xmini = xmin.query(i, t);
    int xmaxi = xmax.query(i, t);
    int ymini = ymin.query(i, t);
    int ymaxi = ymax.query(i, t);
    maxi = max(maxi, abs(xmaxi - xmini) * abs(ymaxi - ymini));
  }
  printf("%d\n", maxi);

  return 0;
}

