int n, d, x[200010], y[200010], b[200010];

PII sorted[200010];
int ans = 0;

struct segtree
{
  int tree[524288];
  int size;

  void reset(bool option = false)
  {
    size = 1;
    while(size < n){
      size <<= 1;
    }

    if(option){
      for(int i = 0; i < size * 2 - 1; i++){
	tree[i] = -INF;
      }
    }
  }

  void update(int where, int val)
  {
    int now = size - 1 + where;
    while(true){
      tree[now] = max(val, now);
      if(now == 0){
	return;
      }
      now = (now - 1) / 2;
    }
  }

  int top()
  {
    return tree[0];
  }
} xmin, xmax, ymin, ymax;

signed main(){
  scanf("%lld%lld", &n, &d);
  for(int i = 0; i < n; i++){
    scanf("%lld%lld%lld", &x[i], &y[i], &b[i]);
  }

  for(int i = 0; i < n; i++){
    sorted[i] = MP(b[i], i);
  }

  sort(sorted, sorted + n);

  xmin.reset(true), xmax.reset(), ymin.reset(true), ymax.reset();

  int start = 0, nowd = 0;
  for(int i = 0; i < n; i++){
    nowd = sorted[i].first;
    xmin.update(i, -x[sorted[i].second]);
    xmax.update(i, x[sorted[i].second]);
    ymin.update(i, -y[sorted[i].second]);
    ymax.update(i, y[sorted[i].second]);

    while(start < n){
      if(sorted[start].first < nowd - d){
	xmin.update(start, -INF);
	xmax.update(start, 0);
	ymin.update(start, -INF);
	ymax.update(start, 0);
	start++;
      }
      else{
	break;
      }
    }

    ans = max(ans, (xmax.top() + xmin.top()) * (ymax.top() + ymin.top()));
  }

  printf("%lld\n", ans);
  return 0;
}

