#define SEG_SIZE (1<<18)
int MinX[2*SEG_SIZE-1];
int MinY[2*SEG_SIZE-1];
int MaxX[2*SEG_SIZE-1];
int MaxY[2*SEG_SIZE-1];
int x[SEG_SIZE];
int y[SEG_SIZE];
int b[SEG_SIZE];
int N,segN;
void init(int *x,int *MinX,int *MaxX) {
	segN=1;
	while(N>segN) segN*=2;
	for(int i=0;i<segN;i++) {
		if(i<N) {
			MinX[i+segN-1]=x[i];
			MaxX[i+segN-1]=x[i];
		}else {
			MinX[i+segN-1]=INT_MAX;
			MaxX[i+segN-1]=INT_MIN;
		}
	}
	for(int i=segN-2;i>=0;i--) {
		MinX[i]=min(MinX[i*2+1],MinX[i*2+2]);
		MaxX[i]=max(MaxX[i*2+1],MaxX[i*2+2]);
	}
}
int min_query(int *MinX,int a,int b,int k,int l,int r) {
	if(a<=l&&r<=b) {
		return MinX[k];
	}else if(l>=b||r<=a) {
		return INT_MAX;
	}
	return min(min_query(MinX,a,b,k*2+1,l,(l+r)/2),min_query(MinX,a,b,k*2+2,(l+r)/2,r));
}
int max_query(int *MaxX,int a,int b,int k,int l,int r) {
	if(a<=l&&r<=b) {
		return MaxX[k];
	}else if(l>=b||r<=a) {
		return INT_MIN;
	}
	return max(max_query(MaxX,a,b,k*2+1,l,(l+r)/2),max_query(MaxX,a,b,k*2+2,(l+r)/2,r));
}
int main() {
	int d;
	scanf("%d %d",&N,&d);
	vector<Star> V;
	for(int i=0;i<N;i++) {
		int x,y,b;
		scanf("%d %d %d",&x,&y,&b);
		V.push_back(Star(b,P(x,y)));
	}
	sort(V.begin(),V.end());
	for(int i=0;i<N;i++) {
		x[i]=V[i].second.first;
		y[i]=V[i].second.second;
		b[i]=V[i].first;
	}
	init(x,MinX,MaxX);
	init(y,MinY,MaxY);
	long long ans=0;
	for(int i=0;i<N;i++) {
		int idx=upper_bound(b,b+N,b[i]+d)-b;
		int minX=min_query(MinX,i,idx,0,0,segN);
		int maxX=max_query(MaxX,i,idx,0,0,segN);
		int minY=min_query(MinY,i,idx,0,0,segN);
		int maxY=max_query(MaxY,i,idx,0,0,segN);
		ans=max(ans,(long long)(maxY-minY)*(maxX-minX));
	}
	printf("%lld\n",ans);
}

