#include <bits/stdc++.h>
using namespace std;
const int INF = 2002002002;
typedef long long LL;

struct DATA {
	int minx, miny, maxx, maxy;
};

struct STAR {
	int x, y, b;
	bool operator < (const STAR &t) const {
		return b < t.b;
	}
};

int n, d;
STAR star[1 << 18];

int N;
DATA data[1 << 19];


void init(int n)
{
	for (N = 1; N < n; N <<= 1);
	fill(data, data + N * 2 - 1, (DATA){INF, INF, -INF, -INF});
}

void update(int i, STAR s)
{
	i += N - 1;
	data[i].minx = min(data[i].minx, s.x);
	data[i].miny = min(data[i].miny, s.y);
	data[i].maxx = max(data[i].maxx, s.x);
	data[i].maxy = max(data[i].maxy, s.y);
	while (i > 0){
		i = (i - 1) / 2;
		data[i].minx = min(data[i].minx, s.x);
		data[i].miny = min(data[i].miny, s.y);
		data[i].maxx = max(data[i].maxx, s.x);
		data[i].maxy = max(data[i].maxy, s.y);
	}
}

DATA query(int a, int b, int k, int l, int r)
{
	//printf("%d %d %d %d %d\n", a, b, k, l, r);
	if (r <= a || b <= l) return (DATA){INF, INF, -INF, -INF};
	if (a <= l && r <= b) return data[k];
	DATA vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
	DATA vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
	return (DATA){min(vl.minx, vr.minx), min(vl.miny, vr.miny), max(vl.maxx, vr.maxx), max(vl.maxy, vr.maxy)};
}

int main()
{
	scanf("%d %d", &n, &d);
	init(n);

	for (int i = 0; i < n; i++){
		scanf("%d %d %d", &star[i].x, &star[i].y, &star[i].b);
		--star[i].x; --star[i].y;
	}
	star[n].b = INF;

	sort(star, star + n);

	for (int i = 0; i < n; i++){
		update(i, star[i]);
	}

	//printf("%d\n", INF);

	LL res = 0;
	for (int l = 0; l < n; l++){
		int r = l;
		while (star[r].b - star[l].b <= d) r++;

		DATA q = query(l, r, 0, 0, N);
		LL t = (LL)(q.maxx - q.minx) * (q.maxy - q.miny);
		res = max(res, t);
	}

	printf("%lld\n", res);

	return 0;
}

#include<bits/stdc++.h>
using namespace std;
typedef pair<int,int> P;
typedef pair<int,P> Pi;
typedef long long int lld;
struct SegmentTree
{
  static const int INF = 1 << 30;

  vector< int > small, big, sum, add;
  int sz;

  SegmentTree(int n)
  {
    sz = 1;
    while(sz < n) sz <<= 1;
    small.assign(2 * sz - 1, INF);
    big.assign(2 * sz - 1, -INF);
    sum.assign(2 * sz - 1, 0);
    add.assign(2 * sz - 1, 0);
  }
  inline void Merge(int k)
  {
    small[k] = min(small[2 * k + 1] + add[2 * k + 1], small[2 * k + 2] + add[2 * k + 2]);
    big[k] = max(big[2 * k + 1] + add[2 * k + 1], big[2 * k + 2] + add[2 * k + 2]);
    sum[k] = sum[2 * k + 1] + add[2 * k + 1] + sum[2 * k + 2] + add[2 * k + 2];
  }
  inline int RangeMaximumQuery(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return(-INF);
    if(a <= l && r <= b) return(big[k] + add[k]);
    int L = RangeMaximumQuery(a, b, 2 * k + 1, l, (l + r) >> 1);
    int R = RangeMaximumQuery(a, b, 2 * k + 2, (l + r) >> 1, r);
    return(max(L, R) + add[k]);
  }
  inline int RangeMinimumQuery(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return(INF);
    if(a <= l && r <= b) return(small[k] + add[k]);
    int L = RangeMinimumQuery(a, b, 2 * k + 1, l, (l + r) >> 1);
    int R = RangeMinimumQuery(a, b, 2 * k + 2, (l + r) >> 1, r);
    return(min(L, R) + add[k]);
  }
  inline int RangeSumQuery(int a, int b, int k, int l, int r)
  {
    if(a >= r || b <= l) return(INF);
    if(a <= l && r <= b) return(sum[k] + add[k]);
    int L = RangeSumQuery(a, b, 2 * k + 1, l, (l + r) >> 1);
    int R = RangeSumQuery(a, b, 2 * k + 2, (l + r) >> 1, r);
    return(L + R + add[k]);
  }
  void RangeAdd(int a, int b, int x, int k, int l, int r)
  {
    if(a >= r || b <= l) return;
    if(a <= l && r <= b) {
      add[k] += x;
      return;
    }
    RangeAdd(a, b, x, 2 * k + 1, l, (l + r) >> 1);
    RangeAdd(a, b, x, 2 * k + 2, (l + r) >> 1, r);
    Merge(k);
  }

  void Update(int k, int x)
  {
    k += sz - 1;
    small[k] = big[k] = sum[k] = x;
    while(k > 0) {
      k = (k - 1) >> 1;
      Merge(k);
    }
  }
  int RangeMaximumQuery(int a, int b)
  {
    return(RangeMaximumQuery(a, b, 0, 0, sz));
  }
  int RangeMinimumQuery(int a, int b)
  {
    return(RangeMinimumQuery(a, b, 0, 0, sz));
  }
  int RangeSumQuery(int a, int b)
  {
    return(RangeSumQuery(a, b, 0, 0, sz));
  }
  void RangeAdd(int a, int b, int x)
  {
    return(RangeAdd(a, b, x, 0, 0, sz));
  }
};

int n,d;
Pi star[200000];
SegmentTree X(200010),Y(200010);

int main(){
  cin >> n >> d;
  for(int i=0;i<n;i++){
    cin >> star[i].second.first >> star[i].second.second >> star[i].first;
  }
  sort(star,star+n);
  for(int i=0;i<n;i++){
    X.Update(i,star[i].second.first);
    Y.Update(i,star[i].second.second);
  }
  int idx = 0;
  lld ans = -1;
  for(int i=0;i<n;i++){
    while(idx < n && star[idx].first - star[i].first <= d) idx++;
    lld maxx = X.RangeMaximumQuery(i,idx);
    lld minx = X.RangeMinimumQuery(i,idx);
    lld maxy = Y.RangeMaximumQuery(i,idx);
    lld miny = Y.RangeMinimumQuery(i,idx);
    ans = max(ans,(maxx-minx)*(maxy-miny));
  }
  cout << ans << endl;
}

