#include <iostream>
#include <algorithm>
#include <map>
using namespace std;

class Star
{
public:
	int x, y, b;
	int boundary;
};
bool operator > (Star s1, Star s2)
{
	return s1.b > s2.b;
}
bool operator < (Star s1, Star s2)
{
	return s1.b < s2.b;
}

int n, d;
Star star[200000];
int maxX = 0, maxY = 0, minX = 0, minY = 0;
int ans = 0;

void calcBoundary()
{
	int boundary = n - 1;
	star[n - 1].boundary = boundary;
	for (int i = n - 2; i >= 0; i--)
	{
		if (star[i + 1].b - star[i].b >= 2) boundary = i;
		star[i].boundary = boundary;
	}

	for (int i = 0; i < n; i++)
	{
		Star s;
		s.b = star[i].b + d;
		boundary = upper_bound(star + i, star + n, s) - star - 1;
		star[i].boundary = min(star[i].boundary, boundary);
	}
}

void solve()
{
	int boundary = -1;
	for (int i = n - 1; i >= 0; i--)
	{
		if (star[i].boundary != boundary)
		{
			boundary = star[i].boundary;
			ans = max((minX - maxX)*(minY - maxY), ans);
			maxX = star[i].x;
			minX = star[i].x;
			maxY = star[i].y;
			minY = star[i].y;

			for (int j = i + 1; j <= star[i].boundary; j++)
			{
				maxX = max(star[j].x, maxX);
				maxY = max(star[j].y, maxY);
				minX = min(star[j].x, minX);
				minY = min(star[j].y, minY);
			}
		}
		else
		{
			maxX = max(star[i].x, maxX);
			maxY = max(star[i].y, maxY);
			minX = min(star[i].x, minX);
			minY = min(star[i].y, minY);
		}
	}

	ans = max((maxX - minX)*(maxY - minY), ans);
}

int main()
{
	cin >> n >> d;
	for (int i = 0; i < n; i++) cin >> star[i].x >> star[i].y >> star[i].b;

	sort(star, star + n);
	calcBoundary();
	solve();

	cout << ans << endl;

	return 0;
}

