        sgx[a].S=min(sgx[a*2+1].S,sgx[a*2+2].S);
    }
}
void upy(ll a,ll p){
    a+=(1LL<<19)-1;
    if(sgy[a].F<p)sgy[a].F=p;
    if(sgy[a].S>p)sgy[a].S=p;
