        sgy[a].S=min(sgy[a*2+1].S,sgy[a*2+2].S);
    }
}
P quex(int a,int b,int l,int r,int k){
    if(r<a||b<l)return P(0,INF);
    if(a<=l&&r<=b)return sgx[k];
    P s=quex(a,b,l,(l+r-1)/2,k*2+1),t=quex(a,b,(l+r+1)/2,r,k*2+2);
