const long long INF = (1LL<<40);

struct SegTree{

  int size;
  vector<long long> vec;

  void init(int n){
    size = 1;
    while(size < n) size <<= 1;
    for(int i=0;i<2*size-1;i++) vec.push_back(INF);
  }

  void update(int k,long long a){
    k += size-1;
    vec[k] = a;
    while(k > 0){
      k = (k-1)/2;
      vec[k] = min(vec[2*k+1],vec[2*k+2]);
    }
  }
  long long query(int a,int b){
    return (query(a, b, 0, 0, size - 1));
  }
  long long query(int a,int b,int k,int l,int r){
    if(r < a || b < l) return INF;
    if(a <= l && r <= b) return vec[k];
    long long nl = query(a,b,2*k+1,l,(l+r)/2);
    long long nr = query(a,b,2*k+2,(l+r)/2+1,r);
    return min(nl, nr);
  }


};

