int getMin(int a,int b,int k,int l,int r,int num){
	if(b <= l || r <= a) return INF;
	if(a <= l && r <= b) return segMin[num][k];
	return min(getMin(a,b,k * 2 + 1,l,(l + r) / 2,num),getMin(a,b,k * 2 + 2,(l + r) / 2,r,num));
}

int getMax(int a,int b,int k,int l,int r,int num){
	if(b <= l || r <= a) return 0;
	if(a <= l && r <= b) return segMax[num][k];
	return max(getMax(a,b,k * 2 + 1,l,(l + r) / 2,num),getMax(a,b,k * 2 + 2,(l + r) / 2,r,num));
}

void updMin(int i,int x,int num){
	i += seg - 1;
	segMin[num][i] = min(x,segMin[num][i]);
	while(i){
		i = (i - 1) / 2;
		segMin[num][i] = min(segMin[num][i * 2 + 1],segMin[num][i * 2 + 2]);
	}
}

void updMax(int i,int x,int num){
	i += seg - 1;
	segMax[num][i] = max(x,segMax[num][i]);
	while(i){
		i = (i - 1) / 2;
		segMax[num][i] = max(segMax[num][i * 2 + 1],segMax[num][i * 2 + 2]);
	}
}

