		segMin[0][i] = INF;
		segMin[1][i];
	}
	vector<int> vec;
	for(int i = 0;i < n;i++){
		cin >> x[i] >> y[i] >> b[i];
		vec.push_back(b[i]);
	}
	sort(vec.begin(),vec.end());
	vec.erase(unique(vec.begin(),vec.end()),vec.end());
	for(int i = 0;i < n;i++){
		int pos = lower_bound(vec.begin(),vec.end(),b[i]) - vec.begin();
		updMin(pos,x[i],0);
		updMax(pos,x[i],0);
		updMin(pos,y[i],1);
		updMax(pos,y[i],1);
	}
