class RMQ{
public:
	ll n;
	vector<ll> dat;
	RMQ(ll n_){
		n = 1; while(n < n_) n *= 2;
		dat = vector<ll>(2*n-1,INT_MAX);
	}

	//dat[k] -> a
	void update(int k, int a){
		k += n-1;
		dat[k] = a;
		while(k > 0){
			k = (k-1)/2;
			dat[k] = min(dat[k*2+1], dat[k*2+2]);
		}
	}

	ll query(int a, int b, int k, int l, ll r){
		if(r <= a || b <= l) return INT_MAX;
		if(a <= l && r <= b) return dat[k];
		else return min(query(a, b, k*2+1, l, (l+r)/2), query(a, b, k*2+2, (l+r)/2, r));
	}

	//[a,b) <- coution a~(b-1)
	ll getMin(int a, int b){
		return query(a,b,0,0,n);
	}
};

