#include<limits>
#define rep(i,a) for(int i=0;i<(a);++i)
#define all(a) (a).begin(), (a).end()

typedef long long ll;
typedef std::pair<ll, ll> Pt;

struct Dat
{ 
	ll x, y, b;

	Dat( ll x, ll y, ll b )
	:	x(x), y(y), b(b)
	{}

	bool operator< ( const Dat &rhs ) const
	{ return b < rhs.b; }
};

Pt minPt( const Pt &lhs, const Pt &rhs )
{ return Pt( std::min( lhs.first, rhs.first ), std::min( lhs.second, rhs.second ) ); }

Pt maxPt( const Pt &lhs, const Pt &rhs )
{ return Pt( std::max( lhs.first, rhs.first ), std::max( lhs.second, rhs.second ) ); }

struct Pts
{ 
	Pt lb, rt;

	Pts()
	{}

	Pts( const Pt &lb, const Pt &rt )
	:	lb(lb), rt(rt)
	{}

	bool operator< ( const Pts &rhs ) const
	{ return std::make_pair( lb, rt ) < std::make_pair( rhs.lb, rhs.rt ); }

	ll area() const
	{ return (rt.first-lb.first)*(rt.second-lb.second); }
};

const int MAX_N = 200000;
const ll INF = std::numeric_limits<ll>::max()>>2;

int N, d;
std::vector<Dat> vs;

struct SegTree
{
	std::size_t sz;
	std::vector<Pts> seg;

	SegTree( std::size_t n )
	{
		sz = 1;
		while( sz < n )
			sz <<= 1;

		seg.resize( 2*sz-1 );
		rep( i, seg.size() )
		{
			seg[i].lb = Pt( INF, INF );
			seg[i].rt = Pt( 0, 0 );
		}
	}

	void update( int k, const Dat &x )
	{
		k += sz-1;
		seg[k].lb = minPt( seg[k].lb, Pt( x.x, x.y ) );
		seg[k].rt = maxPt( seg[k].rt, Pt( x.x, x.y ) );

		while( k )
		{
			k = k-1>>1;
			seg[k] = Pts( minPt( seg[2*k+1].lb, seg[2*k+2].lb ), maxPt( seg[2*k+1].rt, seg[2*k+2].rt ) );
		}

		return;
	}

	Pts query( int a, int b, int k, int l, int r )
	{
		if( b <= l || r <= a )
			return Pts( Pt( INF, INF ), Pt( 0, 0 ) );
		else if( a <= l && r <= b )
			return seg[k];

		Pts pl = query( a, b, 2*k+1, l, l+r>>1 ),
		    pr = query( a, b, 2*k+2, l+r>>1, r );

		return Pts( minPt( pl.lb, pr.lb ), maxPt( pl.rt, pr.rt ) );
	}
};

int main()
{
	scanf( "%d%d", &N, &d );
	rep( i, N )
	{
		ll x, y, b;
		scanf( "%lld%lld%lld", &x, &y, &b );
		vs.push_back( Dat( x, y, b ) );
	}

	std::sort( all(vs) );

	SegTree st( N );

	rep( i, N )
		st.update( i, vs[i] );

	int s = 0, t = 0;
	ll ans = 0;

	for( s = 0; s < N; ++s )
	{
		while( t < N && vs[t].b-vs[s].b <= d )
			++t;
		if( t == N-1 )
			++t;

		ans = std::max( ans, st.query( s, t, 0, 0, st.sz ).area() );
	}

	printf( "%lld\n", ans );

	return 0;
}

