        init(n_);
    }
    void update(int x,int val){
        x+=n-1;
        dat[x]=val;
        while(x){
            x=(x-1)/2;
            dat[x]=max(dat[x*2+1],dat[x*2+2]);
        }
    }
    int getMax(int a,int b,int k=0,int l=0,int r=-1){
        if(r==-1)r=n;
        if(a<=l&&b>=r)return dat[k];
        if(a>=r||b<=l)return INT_MIN;
        int v1=getMax(a,b,k*2+1,l,(l+r)/2);
        int v2=getMax(a,b,k*2+2,(l+r)/2,r);
        return max(v1,v2);
    }
};
