template<int SZ>
class SegMax {
	public:
		cp2 seg[2*SZ];

		SegMax(){
			fill( seg, seg+2*SZ, cp2( 0, 0 ) );
		}

		void ffill(){
			fill( seg, seg+2*SZ, cp2( -mod, -mod ) );
		}

		void upd( int p, cp2 value ){
			for( seg[p += SZ] = value; p > 1; p >>= 1 ) seg[p>>1] = max( seg[p], seg[p^1] );
		}

		cp2 query( int l, int r ){
			cp2 res = cp2( -mod, -mod );
			for( l += SZ, r += SZ; l < r; l >>= 1, r >>= 1 ){
				if( l&1 ) res = max( res, seg[l++] );
				if( r&1 ) res = max( res, seg[--r] );
			}
			return res;
		}
};




