#define FOR(i,a,b) for(int i=(a);i<(b);i++)
#define REP(i,j) FOR(i,0,j)

typedef long long ll;
typedef std::tuple<int,int> T;

const int INF = 1001001001, MAX_N = 200000;

class SegmentTree{
public:
    SegmentTree(){}
    void init(const std::vector<T>& v, const int n){
        size = 1;
        while(size < n){
            size *= 2;
        }

        REP(i, size*2-1){
            seg1[i] = INF;
            seg2[i] = -INF;
        }
        REP(i, v.size()){
            update(i, std::get<1>(v[i]));
        }
    }
    void update(int k, int v){
        k += size - 1;
        seg1[k] = v;
        seg2[k] = v;

        while(k > 0){
            k = (k-1) / 2;
            seg1[k] = std::min(seg1[k*2+1], seg1[k*2+2]);
            seg2[k] = std::max(seg2[k*2+1], seg2[k*2+2]);
        }
    }
    int queryMin(int l, int r){
        return _queryMin(l, r, 0, 0, size);
    }
    int queryMax(int l, int r){
        return _queryMax(l, r, 0, 0, size);
    }
private:
    int _queryMax(int a, int b, int k, int l, int r){
        if(b <= l || r <= a){return -INF;}
        if(a <= l && r <= b){return seg2[k];}
        return std::max(_queryMax(a, b, 2*k+1, l, (l+r)/2),
                        _queryMax(a, b, 2*k+2, (l+r)/2, r));
    }
    int _queryMin(int a, int b, int k, int l, int r){
        if(b <= l || r <= a){return INF;}
        if(a <= l && r <= b){return seg1[k];}
        return std::min(_queryMin(a, b, 2*k+1, l, (l+r)/2),
                        _queryMin(a, b, 2*k+2, (l+r)/2, r));
    }
    int seg1[MAX_N*4], seg2[MAX_N*4], size;
};

int N, d;
std::vector<T> xs, ys;
SegmentTree xt, yt;

ll f(const int b, const std::vector<T>& v, SegmentTree& t){
    int lb = std::lower_bound(v.begin(), v.end(), std::make_tuple(b, -1)) - v.begin(),
        ub = std::upper_bound(v.begin(), v.end(), std::make_tuple(b+d, INF)) - v.begin();
    return t.queryMax(lb, ub) - t.queryMin(lb, ub);
}

int main(){
    scanf("%d %d", &N, &d);

    for(int i=0;i<N;i++){
        int x, y, b;
        scanf("%d %d %d", &x, &y, &b);

        xs.push_back(std::make_tuple(b, x));
        ys.push_back(std::make_tuple(b, y));
    }

    std::sort(xs.begin(), xs.end());
    std::sort(ys.begin(), ys.end());

    // for(const T& t : xs){
    //     printf("%d, %d\n", std::get<0>(t), std::get<1>(t));
    // }
    // puts(std::string(45, '-').c_str());
    // for(const T& t : ys){
    //     printf("%d, %d\n", std::get<0>(t), std::get<1>(t));
    // }
    // puts(std::string(45, '-').c_str());

    xt.init(xs, N);
    yt.init(ys, N);

    ll res = 0ll;
    for(int i=0,j=0;i<N;){
        // printf("%d: %lld, %lld\n", std::get<0>(xs[i]), f(std::get<0>(xs[i]), xs, xt), f(std::get<0>(xs[i]), ys, yt));
        res = std::max(res, f(std::get<0>(xs[i]), xs, xt) * f(std::get<0>(xs[i]), ys, yt));

        while(j < N && std::get<0>(xs[j]) == std::get<0>(xs[i])){
            j++;
        }
        i = j;
    }
    printf("%lld\n", res);
}

