struct SegTree
{
    using Monoid = int(*)(int, int);

    vector<int> dat;
    int size;
    const int SENTINEL; // , 
    Monoid f;

    explicit SegTree(int n, int sntinel, const Monoid &monoid)
        : SENTINEL(sntinel), f(monoid)
    {
        for(size=1; size<n; size*=2);
        dat.assign(size*2 + 5, SENTINEL);
    }

    void update(int i, int v)
    {
        dat[i+=size-1] = v;
        while (i > 0)  {
            i = (i-1) / 2;
            dat[i] = f(dat[i*2+1], dat[i*2+2]);
        }
    }

    int query(int a, int b, int k, int l, int r)
    {
        if (r <= a || b <= l) return SENTINEL;
        if (a <= l && r <= b) return dat[k];
        int mid = (l + r) / 2;
        return f( query(a,b, 2*k+1, l, mid), query(a,b, 2*k+2, mid, r) );
    }

    int query(int a, int b) { return query(a,b, 0, 0, size); }

};

