struct SEG {
	int aryN;
	vector<PII> seg;

	SEG(){}
	SEG(const VI &v){
		aryN = (int)pow(2, (int)ceil(log2(SIZE(v))));
		seg.resize(2*aryN-1, { -1, INF });
		rep(i, 0, SIZE(v)) seg[i+aryN-1] = { v[i], v[i] };
		reper(i, aryN-2, 0) seg[i].first = max(seg[2*i+1].first, seg[2*i+2].first), seg[i].second = min(seg[2*i+1].second, seg[2*i+2].second);
	}
	void update(int x, PII p){
		x += (aryN-1);
		seg[x] = p;
		while(x > 0){
			x = (x-1)/2;
			seg[x].first = max(seg[2*x+1].first, seg[2*x+2].first);
			seg[x].second = min(seg[2*x+1].second, seg[2*x+2].second);
		}
	}
	int getMax(int a, int b, int k = 0, int l = 0, int r = -1){
		if(r < 0) r = aryN;
		if(r <= a || b <= l) return -1;
		if(a <= l && r <= b) return seg[k].first;
		int vl = getMax(a, b, 2*k+1, l, (l+r)/2);
		int vr = getMax(a, b, 2*k+2, (l+r)/2, r);
		return max(vl, vr);
	}
	int getMin(int a, int b, int k = 0, int l = 0, int r = -1){
		if(r < 0) r = aryN;
		if(r <= a || b <= l) return INF;
		if(a <= l && r <= b) return seg[k].second;
		int vl = getMin(a, b, 2*k+1, l, (l+r)/2);
		int vr = getMin(a, b, 2*k+2, (l+r)/2, r);
		return min(vl, vr);
	}
};


